import type { Pack, Prompt } from '@/domain';

// Helper to create prompts with auto-generated IDs
let promptCounter = 0;
function createPrompt(
  type: 'truth' | 'dare',
  intensity: 1 | 2 | 3,
  topics: string[],
  textEn: string,
  textTr: string,
  constraints: string[] = [],
  durationSec?: number
): Prompt {
  return {
    id: `default-${type[0]}-${++promptCounter}`,
    type,
    intensity,
    topics,
    constraints,
    durationSec,
    text: { en: textEn, tr: textTr },
  };
}

