// ============ Create the pack ============
export const DEFAULT_PACKS: Pack[] = [
  {
    id: 'core-light-v1',
    name: {
      en: 'Core Pack',
      tr: 'Ana Paket',
    },
    description: {
      en: 'The essential collection of truth and dare prompts for all occasions',
      tr: 'Her durum için temel doğruluk mu cesaret mi soruları',
    },
    prompts: allPrompts,
    isDefault: true,
    isEnabled: true,
  },
];

// Get total prompt count
export function getTotalPromptCount(): number {
  return DEFAULT_PACKS.reduce((sum, pack) => sum + pack.prompts.length, 0);
}
